// Imports
import {gsap} from 'gsap';
import {map, lerp, clamp, getMousePos} from './utils';

// Images
const images = Object.entries(require('../assets/img/*.jpg'));

// Track Mouse Position
let mousepos = { x: 0, y: 0};
// Cache the Mouse Position
let mousePosCache = mousepos;
let direction = {
    x: mousePosCache.x - mousepos.x,
    y: mousePosCache.y - mousepos.y
};

// Update mouse position when moving the mouse
window.addEventListener('mousemove', ev => mousepos = getMousePos(ev));

// MenuItem
export default class MenuItem {

    // constructor
    constructor(el, inMenuPosition, animatableProperties) {

        // el is the <a> with class 'menu_item'
        this.DOM = {el:el};

        // position in the Menu
        this.inMenuPosition = inMenuPosition;

        // menu item properties that will animate as we
        // move the mouse around the menu
        this.animatableProperties = animatableProperties;

        // the item text
        this.DOM.textInner = this.DOM.el.querySelector('.menu_item_inner_text');

        // create the image structure
        this.layout();

        // initialise some events
        this.initEvents();
    }

    // Layout
    // create the image structure
    // we want to add/append to the menu item the following html:
    // <div class="hover_reveal">
    //   <div class="hover_reveal_inner" style="overflow: hidden;">
    //     <div class="hover_reveal_img" style="background-image: url(pathToImage);">
    //     </div>
    //   </div>
    // </div>
    layout() {
         // this is the element that gets its position animated (and perhaps other properties like the rotation etc..)
        this.DOM.reveal = document.createElement('div');
        this.DOM.reveal.className = 'hover_reveal';
        this.DOM.reveal.style.transformOrigin = '0% 0%';

        // the next two elements could actually be only one, the image element
        // adding an extra wrapper (revealInner) around the image element with 
        // overflow hidden, gives us the possibility to scale the image inside
        this.DOM.revealInner = document.createElement('div');
        this.DOM.revealInner.className = 'hover_reveal_inner';
        this.DOM.revealImage = document.createElement('div');
        this.DOM.revealImage.className = 'hover_reveal_img';
        this.DOM.revealImage.style.backgroundImage = `url(${images[this.inMenuPosition][1]})`;

        this.DOM.revealInner.appendChild(this.DOM.revealImage);
        this.DOM.reveal.appendChild(this.DOM.revealInner);
        this.DOM.el.appendChild(this.DOM.reveal);
    }

    // getMouseArea
    getMouseArea() {
        return this.bounds.el.top + this.bounds.el.height / 2 <= window.innerHeight / 2 ? 'up' : 'down';
    }

    // calcBounds
    // calculate the position/size of both 
    // the menu item and reveal element
    calcBounds() {
        this.bounds = {
            el: this.DOM.el.getBoundingClientRect(),
            reveal: this.DOM.reveal.getBoundingClientRect()
        };
    }

    // initEvents
    // bind some events
    initEvents() {
        this.mouseenterFn = (ev) => {

            // show the image element
            this.showImage();
            this.firstRAFCycle = true;
            this.DOM.reveal.style.transformOrigin = `0% ${this.mouseArea === 'up' ? 0 : 100}%`;

            // start the render loop animation (rAF)
            this.loopRender();
        };

        this.mouseleaveFn = () => {
            // stop the render loop animation (rAF)
            this.stopRendering();
            // hide the image element
            this.hideImage();
        }

        this.DOM.el.addEventListener('mouseenter', this.mouseenterFn);
        this.DOM.el.addEventListener('mouseleave', this.mouseleaveFn);
    }

    // showImage
    showImage() {

        // kill any current tweens
        gsap.killTweensOf(this.DOM.revealInner);
        gsap.killTweensOf(this.DOM.revealImage);

        this.tl = gsap.timeline({
            onStart: () => {
                // show both image and its parent element
                this.DOM.reveal.style.opacity = this.DOM.revealInner.style.opacity = 1;
                // set a high z-index value so image appears
                // on top of other elements
                gsap.set(this.DOM.el, {zIndex : images.length});
            }
        })
        // animate the image wrap
        .to(
            this.DOM.revealInner,
            0.6,
            {
                ease: 'Expo.easeOut',
                startAt: {scale : 0.6},
                scale: 1
            }
        )
        // animate the image element
        .to(
            this.DOM.revealImage,
            0.6,
            {
                ease: 'Expo.easeOut',
                startAt: {scale : 1.4},
                scale: 1
            },
            0
        );
    }

    // hideImage
    // hide the image element
    hideImage() {

        // kill any current tweens
        gsap.killTweensOf(this.DOM.revealInner);
        gsap.killTweensOf(this.DOM.revealImage);

        this.tl = gsap.timeline({
            onStart: () => {
                gsap.set(this.DOM.el, {zIndex : 1});
            },
            onComplete: () => {
                gsap.set(this.DOM.reveal, {opacity: 0});
            }
        })
        .to(
            this.DOM.revealInner,
            0.6,
            {
                ease: 'Expo.easeOut',
                scale: 0.6,
                opacity: 0
            }
        )
        .to(
            this.DOM.revealImage,
            0.6,
            {
                ease: 'Expo.easeOut',
                scale: 1.4
            },
            0
        );
    }

    // loopRender
    // start the render loop animation (rAF)
    loopRender() {
        if(! this.requestId) {
            this.requestId = requestAnimationFrame(() => this.render());
        }
    }

    // stopRendering
    // stop the render loop animation (rAF)
    stopRendering() {
        if( this.requestId) {
            window.cancelAnimationFrame(this.requestId);
            this.requestId = undefined;
        }
    }

    // render
    render() {

        this.requestId = undefined;

        if(this.firstRAFCycle) {
            // calculate position/sizes the first time
            this.calcBounds();
            this.mouseArea = this.getMouseArea();
        }

        // calculate the mouse distance (current v previous cycle)
        const mouseDistanceX = clamp(
            Math.abs(mousePosCache.x - mousepos.x),
            0,
            100
        );

        // direction where mouse is moving
        direction = {
            x: mousePosCache.x - mousepos.x,
            y: mousePosCache.y - mousepos.y
        };

        // update cache values
        mousePosCache = {
            x: mousepos.x,
            y: mousepos.y
        }

        // new translation values
        this.animatableProperties.tx.current = Math.abs(mousepos.x - this.bounds.el.left);
        this.animatableProperties.ty.current = this.mouseArea === 'up' ? Math.abs(mousepos.y - this.bounds.el.top) : Math.abs(mousepos.y - this.bounds.el.top) - this.bounds.reveal.height;

        // new rotation value
        this.animatableProperties.rotation.current = this.firstRAFCycle ? 0 : map(mouseDistanceX,0,175,0,direction.x < 0 ? this.mouseArea === 'up' ? 60 : -60 : this.mouseArea === 'up' ? -60 : 60);

        // new filter value
        this.animatableProperties.brightness.current = this.firstRAFCycle ? 1 : map(mouseDistanceX,0,100,1,8);

        // set up the interpolated values
        // for the first cycle, both the interpolated values need to be
        // the same so there's no 'lerped' animation between the previous and current state
        this.animatableProperties.tx.previous = this.firstRAFCycle ? this.animatableProperties.tx.current : lerp(this.animatableProperties.tx.previous, this.animatableProperties.tx.current, this.animatableProperties.tx.amt);
        this.animatableProperties.ty.previous = this.firstRAFCycle ? this.animatableProperties.ty.current : lerp(this.animatableProperties.ty.previous, this.animatableProperties.ty.current, this.animatableProperties.ty.amt);
        this.animatableProperties.rotation.previous = this.firstRAFCycle ? this.animatableProperties.rotation.current : lerp(this.animatableProperties.rotation.previous, this.animatableProperties.rotation.current, this.animatableProperties.rotation.amt);
        this.animatableProperties.brightness.previous = this.firstRAFCycle ? this.animatableProperties.brightness.current : lerp(this.animatableProperties.brightness.previous, this.animatableProperties.brightness.current, this.animatableProperties.brightness.amt);

        // set styles
        gsap.set(
            this.DOM.reveal,
            {
                x: this.animatableProperties.tx.previous,
                y: this.animatableProperties.ty.previous,
                rotation: this.animatableProperties.rotation.previous,
                filter: `brightness(${this.animatableProperties.brightness.previous})`
            }
        );

        // loop
        this.firstRAFCycle = false;
        this.loopRender();

    }
}
